SELECT departments.DEPARTMENT_NAME, locations.LOCATION_ID, locations.STREET_ADDRESS, locations.CITY, locations.STATE_PROVINCE, countries.COUNTRY_NAME FROM locations INNER JOIN countries ON locations.COUNTRY_ID = countries.COUNTRY_ID INNER JOIN departments ON departments.LOCATION_ID = locations.LOCATION_ID;;
SELECT employees.FIRST_NAME, employees.LAST_NAME, departments.DEPARTMENT_ID, departments.DEPARTMENT_NAME FROM employees INNER JOIN departments ON employees.DEPARTMENT_ID = departments.DEPARTMENT_ID;
SELECT employees.FIRST_NAME, employees.LAST_NAME, employees.JOB_ID, departments.DEPARTMENT_ID, departments.DEPARTMENT_NAME FROM employees INNER JOIN departments ON employees.DEPARTMENT_ID = departments.DEPARTMENT_ID INNER JOIN locations ON departments.LOCATION_ID = locations.LOCATION_ID WHERE locations.CITY = "London";
SELECT departments.DEPARTMENT_NAME, COUNT(employees.EMPLOYEE_ID) FROM departments INNER JOIN employees ON departments.DEPARTMENT_ID = employees.DEPARTMENT_ID GROUP BY departments.DEPARTMENT_NAME;